sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var startDt = current.getValue('u_rent_date_start');
    var endDt = current.getValue('u_rent_date_end');
    var yachtId =current.getValue('u_boat');
	var yachtName=current.getDisplayValue('u_boat');
	gs.info('the yactch selected'+ yachtId);
    var lstRent = new GlideRecord("x_295413_yachtclub_u_rent");
    var myQuery = 'u_boat=' + yachtId + '^';
    myQuery += 'u_rent_date_start<=' + startDt + '^';
    myQuery += 'u_rent_date_end>=' + startDt;
    myQuery += '^NQ';
    myQuery += 'u_boat=' + yachtId + '^';
    myQuery += 'u_rent_date_start<=' + endDt + '^';
    myQuery += 'u_rent_date_end>=' + endDt;
    lstRent.addEncodedQuery(myQuery);
    lstRent.query();
    if (lstRent.next()) {
        gs.addErrorMessage('The Yacht' + yachtName + 'dates are not available between the dates' + startDt + '-' + endDt);

        current.setAbortAction(true);
    }

    // if the yacht is booked for that date,Display yacht is unavailable 

})(current, previous);
