sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var currYacht = current.u_boat;
    var yachtName = current.u_boat.getDisplayValue('u_model');
    var thRent = new GlideRecord("x_295413_yachtclub_u_rent");

    thRent.addEncodedQuery('u_boat=' + currYacht + '^u_status=2^ORu_status=3');
	//u_boat=2d59309d07fe2010adadff1d7c1ed061
    thRent.query();
    var ytCount = thRent.getRowCount();
    gs.info(ytCount); // if there are no records set yacht status to available 
    if (ytCount == 0) {

        var gr = new GlideRecord('x_295413_yachtclub_u_yacht');
        gr.addEncodedQuery('sys_id=' + currYacht);
        gr.query();
        if (gr.next()) {
            gr.setValue('u_state', '1');

            gr.update();

        }
        //var yachtRef=current.u_boat.getRefRecord();
        //yachtRef.setValue('u_status','1');
        //yachtRef.update();
        //gs.addInfoMessage('Yacht status is updated ');


    } else {
        gs.addInfoMessage('There are rent records for  ' + yachtName);
    }



})(current, previous);
