sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var startDt = g_form.getValue('u_rent_date_start'); 
    var endDt = g_form.getValue('u_rent_date_end'); 
    var ajax = new GlideAjax('rentDateUtility');
    ajax.addParam('sysparm_name', 'validateEndDate');
	ajax.addParam('sysparm_sdt', startDt);
    ajax.addParam('sysparm_edt', endDt);
    ajax.getXML(ValidateDate);

}

function ValidateDate(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    if (answer == 1) {
        g_form.showFieldMsg('u_rent_date_end', "The end date can't be before start date",'error');
    } else if (answer == 0) {
        g_form.showFieldMsg('u_rent_date_end','endDate is valid','info');
    }



}
