sys_script_include.script:
var showStartDate = Class.create();
showStartDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDateInfo: function() {

        var yacht = this.getParameter('sysparm_boat');
        var result1 = [];
        gs.info('---> yacht Parm: ' + yacht);

        gs.info('the client script Available date for yacht triggered the  ShowStartdate script include');


        var grRent = new GlideRecord('x_295413_yachtclub_u_rent');
        gs.info('The Glide Record is initiated ');

        grRent.addQuery('u_boat', yacht);

        grRent.query();

        while (grRent.next()) {

            var obj1 = {};
            obj1.yacht = grRent.u_boat.getDisplayValue();
            obj1.startDate = grRent.u_rent_date_start.getDisplayValue();
            obj1.endDate = grRent.u_rent_date_end.getDisplayValue();

            result1.push(obj1);
        }

        gs.info(result1);
        var date_sort_asc = function(date1, date2) {
            if (GlideDateTime(date1.startDate) > GlideDateTime(date2.startDate)) return 1;

            if (GlideDateTime(date1.startDate) < GlideDateTime(date2.startDate)) return -1;

            return 0;

        };
        result1.sort(date_sort_asc);

        var currentDate = new GlideDate();
        gs.info(currentDate);
        var duration = new GlideDuration('3 00:00:00');
        gs.info(duration.getDisplayValue());
        for (var i = 0; i < result1.length; i++) { //Loop into the array
            gs.info("the selected  " + result1[i].yacht + " Yacht is booked from " + result1[i].startDate + " to " + result1[i].endDate);
            var sgd1 = new GlideDate();
            sgd1.setDisplayValue(result1[i].startDate);
            var durationDiff = GlideDate.subtract(currentDate, sgd1);
            gs.info('The start date is after ' + durationDiff.getDisplayValue());

            if (durationDiff > duration) {
                gs.info('The start date is greater than 3 days');
                var gdt = new GlideDateTime(currentDate);
                //gs.info(gdt);     
                gdt.addDaysUTC(1);
                var stDat = gdt.getDate();
                gs.info('The possible available date is   ' + stDat);

                //gs.info(gdt.getDate());
                break;

            } else {
                gs.info('this start date is LESS than 3 days ');
                currentDate = new GlideDate();
                currentDate.setDisplayValue(result1[i].endDate);
                gs.info('the current date is date ' + currentDate);


            }

        }
        gs.info("Final results The selected" + result1[i].yacht + " is available earlie from  " + stDat);

        return stDat;


    },

    type: 'showStartDate'
});
