sys_script_include.script:
var showStartDate = Class.create();
showStartDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDateInfo: function() {

        var yacht = this.getParameter('sysparm_boat');
        var result1 = [];
        gs.info('---> yacht Parm: ' + yacht);

        gs.info('the client script Available date for yacht triggered the  ShowStartdate script include');


        var grRent = new GlideRecord('x_295413_yachtclub_u_rent');
        gs.info('The Glide Record is initiated ');

        grRent.addQuery('u_boat', yacht);

        grRent.query();

        while (grRent.next()) {
            gs.info('The while is triggered');
            var obj1 = {};
            obj1.yacht = grRent.u_boat.getDisplayValue();
            obj1.startDate= grRent.u_rent_date_start.getDisplayValue();
            obj1.endDate= grRent.u_rent_date_end.getDisplayValue();
            gs.info('The while is completed');
            result1.push(obj1);
        }

        gs.info(result1);

        return JSON.stringify(result1);


    },

    type: 'showStartDate'
});
