sys_script_include.script:
var showStartDate = Class.create();
showStartDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDateInfo: function() {
        var outputs = [];
        var yacht = this.getParameter('sysparm_boat');
        gs.info('the client script Available date for yacht triggered the  ShowStartdate script include');

       // var yachtId = yacht.getUniqueValue();
       gs.info('TESTING: yacht is = ' + yacht);
        var gr = new GlideRecord('x_295413_yachtclub_u_rent');
        gr.addQuery('u_yacht', yacht);
        gr.query();

        while (gr.next()) {
            var ids = {
                'u_yacht': gr.getValue('u_yacht'),
                'u_rent_date_start': gr.getValue('u_rent_date_start'),

            };
            outputs.push(ids);
        }
       var chr = { "output": outputs };


        return JSON.stringify(chr);

    },

    type: 'showStartDate'
});
