sys_script_include.script:
var rentDateUtility = Class.create();
rentDateUtility.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    validateStartDate: function() {
        var se_start_date = this.getParameter('sysparm_fdt');
        var currentDate = new GlideDate();
        if (se_start_date != '' && se_start_date < currentDate) //
        {
            return 1; // Start Date Entered is in past

        } else
            return 0;
    },

    validateEndDate: function() {
        var se_start_date = this.getParameter('sysparm_sdt');
        var se_end_date = this.getParameter('sysparm_edt');
		if (se_start_date == '' && se_end_date != '' ){
			return 2;
		}	else if (se_start_date != '' && se_end_date < se_start_date) 
        {
            return 1; // End Date Entered is before start date

        } else if (se_start_date != '' && se_end_date >= se_start_date)
            return 0;
    },

    type: 'rentDateUtility '
});
